func:
let data = msg.payload.RfReceived.Data
let plug = flow.get("button." + data).plug
let label = ""

if(flow.get("plug." + plug + ".status") === "on") {
    flow.set("plug." + plug + ".status", "off")
    msg.payload = flow.get("plug." + plug + ".off" )
    node.status({fill:"gray",shape:"ring",text: label + plug.toUpperCase() + " off"})
} else {
    flow.set("plug." + plug + ".status", "on")
    msg.payload = flow.get("plug." + plug + ".on" )
    node.status({fill:"yellow",shape:"dot",text: label + plug.toUpperCase() + " on"})
}

delete msg.topic;
delete msg.retain;
delete msg.qos;
return msg;
